/**
 * @role Récupère l'heure et la date du système, les formate, et les renvoie en sortie.
 * @output {string} Outputs.Hour - Heure au format "HH:MM".
 * @output {string} Outputs.DayName - Jour de la semaine (en français).
 * @output {string} Outputs.DayNumMonth - Numéro du jour et mois (ex: "27 avril").
 */
try {
    const now = new Date();

    // Format HH:MM avec zéro initial
    const pad = n => n < 10 ? '0' + n : '' + n;
    Outputs.Hour = pad(now.getHours()) + ':' + pad(now.getMinutes());

    // Jours et mois en français
    const jours = [
        "dimanche", "lundi", "mardi", "mercredi", "jeudi", "vendredi", "samedi"
    ];
    const mois = [
        "janvier", "février", "mars", "avril", "mai", "juin",
        "juillet", "août", "septembre", "octobre", "novembre", "décembre"
    ];

    Outputs.DayName = jours[now.getDay()];
    Outputs.DayNumMonth = now.getDate() + ' ' + mois[now.getMonth()];

    Outputs.Success();
} catch (err) {
    Outputs.Failure("Erreur lors de la récupération de la date/heure : " + err.message);
}

