/**
 * @role Envoie une requête POST au webhook n8n spécifié et corrige la sortie pour ne retourner qu'une string si possible.
 * @param {object} Inputs.Data - Données à envoyer dans le body (JSON).
 */
const data = Inputs.Data;
if (!data) {
    Outputs.Failure("Aucune donnée à envoyer (Inputs.Data manquant)");
    return;
}

const WEBHOOK_URL = "https://n8n-dev.neosenior.fr/webhook/3d7d3cee-9217-45be-9c07-11d4fa17a37a";

(async () => {
    try {
        const response = await fetch(WEBHOOK_URL, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(data)
        });

        if (!response.ok) {
            Outputs.Failure(`Erreur HTTP ${response.status}`);
            return;
        }

        // On tente de lire la réponse JSON, sinon texte brut
        let result;
        try {
            result = await response.json();
        } catch {
            result = await response.text();
        }

        // Correction : extraire la string finale si la structure est imbriquée
        let text;
        if (typeof result === "string") {
            text = result;
        } else if (result && typeof result.output === "string") {
            text = result.output;
        } else if (
            result &&
            typeof result.output === "object" &&
            typeof result.output.output === "string"
        ) {
            text = result.output.output;
        } else {
            text = ""; // ou éventuellement Outputs.Failure("Sortie inattendue")
        }

        Outputs.Response = text;
        Outputs.Success();
    } catch (err) {
        Outputs.Failure("Erreur lors de l'appel au webhook : " + err.message);
    }
})();

