/**
 * Joue le son "clic0.wav" stocké à la racine du projet.
 * @output Outputs.Success - Signal de succès si le son est joué.
 * @output Outputs.Failure - Signal d'échec avec message.
 */
const audioFileName = "clic0.wav";
try {
    // Vérifie si l'instance Audio existe déjà (pour éviter de la recréer à chaque clic)
    if (!this.audio) {
        // Chemin relatif depuis la racine du projet
        this.audio = new Audio(audioFileName);
    }
    this.audio.currentTime = 0; // Rewind pour pouvoir rejouer instantanément
    this.audio.play();
    Outputs.Success();
} catch (e) {
    Outputs.Failure("Erreur lors de la lecture du son : " + e.message);
}

