/**
 * @role Envoie une requête POST au webhook n8n spécifié.
 * @param {object} Inputs.Data - Données à envoyer dans le body (JSON).
 */
const data = Inputs.Data;
if (!data) {
    Outputs.Failure("Aucune donnée à envoyer (Inputs.Data manquant)");
    return;
}

const WEBHOOK_URL = "https://n8n-dev.neosenior.fr/webhook/3d7d3cee-9217-45be-9c07-11d4fa17a37a";

(async () => {
    try {
        const response = await fetch(WEBHOOK_URL, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(data)
        });

        if (!response.ok) {
            Outputs.Failure(`Erreur HTTP ${response.status}`);
            return;
        }

        // On tente de lire la réponse JSON, sinon texte brut
        let result;
        try {
            result = await response.json();
        } catch {
            result = await response.text();
        }

        Outputs.Response = result;
        Outputs.Success();
    } catch (err) {
        Outputs.Failure("Erreur lors de l'appel au webhook : " + err.message);
    }
})();

/**
 * @role Extrait la chaîne de caractères "output" d'un objet reçu du webhook n8n pour affichage dans un composant UI Text.
 * @param {object} Inputs.output - Objet contenant la clé "output" (string).
 * @output {string} Outputs.Text - Le texte à afficher dans le composant UI Text.
 */
const outputObj = Inputs.output;
if (!outputObj || typeof outputObj.output !== "string") {
    Outputs.Failure("Entrée 'output.output' manquante ou invalide");
    return;
}

Outputs.Text = outputObj.output;
Outputs.Success();

